mapper.select:
select
id, infoType, title, startTime, endTime, checkUser, checkUserId, distOrg, distOrgId, descr, sendUser, sendUserId
from fbsys.appform


        and id = #{id}


        and infotype = #{infotype}


        and title = #{title}


        and startTime = #{starttime}


        and endTime = #{endtime}


        and checkUser = #{checkuser}


        and checkUserId = #{checkuserid}


        and distOrg = #{distorg}


        and distOrgId = #{distorgid}


        and descr = #{descr}


        and sendUser = #{senduser}


        and sendUserId = #{senduserid}
